import React, { Component } from 'react';
import axios from 'axios';

export default class Chat extends Component {
    constructor(props) {
        super(props);
        this.state = {
            chatList: [],
            messages: [],
        } // end state
    }  // end constructor

    async componentDidMount() {  // To run on mount
    this.refreshList();
  };

  // Function to update chatList by querying the todos API
  refreshList = () => {
    axios.get("/api/chats").then(res => this.setState({chatList: res.data})).catch(e => console.log(e))
  };

  render() {
        return(
            <div>
            <div id="message-container"></div>
              <form id="form">
                  <label htmlFor="message-input">Message</label>
                  <input type="text" id="message-input"/>
                      <button type="submit" id="send-button">Send</button>
                      <label htmlFor="room-input">Room</label>
                      <input type="text" id="room-input"/>
                          <button type="button" id="room-button">Join</button>
              </form>
            </div>
        )
  };

}  // end Chat Component
