import React, { Component } from 'react';
import axios from 'axios';
import {
    Button,
    Modal,
    ModalHeader,
    ModalBody,
    ModalFooter,
    Form,
    FormGroup,
    Input,
    Label
} from "reactstrap";

export default class Chat extends Component {
    constructor(props) {
        super(props);
        this.state = {
            chatList: [],
            messages: [],
        } // end state
    }  // end constructor

    async componentDidMount() {  // To run on mount
    this.refreshList();
  };

  // Function to update chatList by querying the todos API
  refreshList = () => {
    axios.get("/api/chats").then(res => this.setState({chatList: res.data})).catch(e => console.log(e))
  };

  render() {
        return(
            <div className='col-lg-6 w-50 h-50 align-items-center mx-auto'>
            <div id="message-container" className='col-lg-12'></div>
                <Form>
                      <FormGroup>
                          <Label for='message'>Message</Label>
                          <Input
                            type='text' name='message' id="message"
                            placeholder="Enter Message"
                          />
                      </FormGroup>
                    <Button color='success'>
                        Send
                  </Button>
                      <FormGroup>
                          <Label for='room'>Room</Label>
                          <Input
                            type='text' id="room" name='room'
                             placeholder="room"
                          />
                      </FormGroup>
                    <Button color='success'>
                        Join
                  </Button>
                </Form>
            </div>
        )
  };

}  // end Chat Component
