    ModalBody,
    ModalFooter,
    Form,
    FormGroup,
    Input,
    Label
} from "reactstrap";

export default class Chat extends Component {
    constructor(props) {
        super(props);
        this.state = {
            chatList: [],
            messages: [],
        } // end state
    }  // end constructor

