      ));
  }

  render() {
        return(
            <div className='col-lg-6 w-50 h-50 align-items-center mx-auto'>
            <div id="message-container" className='col-lg-12 border border-info rounded w-100 h-50'></div>
                <Form>
                      <FormGroup>
                          <Label for='message'>Message</Label>
                          <Input
                            type='text' name='message' id="message"
                            placeholder="Enter Message"
                          />
                      </FormGroup>
                    <Button color='success'>
                        Send
                  </Button>
                      <FormGroup>
                          <Label for='room'>Room</Label>
                          <Input
                            type='text' id="room" name='room'
                             placeholder="room"
                          />
                      </FormGroup>
                    <Button color='success'>
                        Join
                  </Button>
                </Form>
            </div>
        )
  };

}  // end Chat Component
