import React, { useState, useEffect } from 'react';
import axios from 'axios';
import {
    Button,
    Modal,
    ModalHeader,
    ModalBody,
    ModalFooter,
    Form,
    FormGroup,
    Input,
    Label
} from "reactstrap";
import socketIOClient from "socket.io-client";
const ENDPOINT = "http://127.0.0.1:3000";


function Chat(){
    const [messages, setMessages] = useState('message 1')

    useEffect(() => {
        const socket = socketIOClient(ENDPOINT);
        socket.on("receive-message", data => {
          setMessages(data);
        });
      }, []);

    return(
            <div className='col-lg-6 w-50 h-50 align-items-center mx-auto'>
            <div id="message-container" className='col-lg-12 border border-info rounded w-100 h-50'>{messages}</div>
                <Form>
                      <FormGroup>
                          <Label for='message'>Message</Label>
                          <Input
                            type='text' name='message' id="message"
                            placeholder="Enter Message"
                          />
                      </FormGroup>
                    <Button color='success'>
                        Send
                  </Button>
                      <FormGroup>
                          <Label for='room'>Room</Label>
                          <Input
                            type='text' id="room" name='room'
                             placeholder="room"
                          />
                      </FormGroup>
                    <Button color='success'>
                        Join
                  </Button>
                </Form>
            </div>
        )
}

